        /// @param in_json 内容至少如下：{"id":123, "botId":456}
        /// @throw IllegalArgumentException
        explicit Group(nlohmann::json in_json);
        /// @note dev: avoid unintentional implicit conversion to nlohmann::json
        Group(bool) = delete;
        ~Group() override = default;
    public:
